INSERT INTO `product` (`id`, `subcategory_id`, `name`, `description`, `rating`, `license_type`, `language`, `developers`, `downloads`, `slug`) VALUES
(1, 1, 'Google Chrome', 'Google Chrome — браузер, разрабатываемый компанией Google на основе свободного браузера Chromium и движка Blink. Первая публичная бета-версия для Windows вышла 2 сентября 2008 года, а первая стабильная — 11 декабря 2008 года. По данным StatCounter, рыночная доля Chrome на август 2022 года составляет ▲65,12 %', 4.7, 'free', 'Русский', 'Google', 0, 'google-chrome'),
(2, 2, 'VirtualBox', 'Oracle VM VirtualBox (ранее Sun VirtualBox, Sun xVM VirtualBox и Innotek VirtualBox) — программное обеспечение для виртуализации, разработанное немецкой фирмой Innotek GmbH. В 2008 году фирма была приобретена компанией Sun Microsystems, а в 2010 году стала частью Oracle Corporation. Поддерживает различные операционные системы, включая Windows, Linux, macOS и др.', 4.7, 'free', 'Мультиязычный', 'Google', 0, 'virtualbox'),
(3, 3, 'CCleaner', 'CCleaner — утилита для чистки мусора в операционных системах Windows, macOS и Android. Позволяет удалять временные и неиспользуемые файлы, очищать кэш браузеров, исправлять ошибки реестра и выполнять другие операции для повышения производительности системы.', 4.7, 'freemium', 'Мультиязычный', 'Google', 0, 'ccleaner'),
(4, 4, 'Mozilla Firefox', 'Mozilla Firefox — свободный браузер с открытым исходным кодом, разрабатываемый сообществом Mozilla. Поддерживает операционные системы Windows, macOS, Linux и Android. Изначально выпущен в 2002 году под названием Phoenix, затем переименован в Mozilla Firebird и, наконец, получил текущее название.', 4.7, 'free', 'Мультиязычный', 'Google', 0, 'mozilla-firefox'),
(5, 5, 'Visual Studio Code', 'Visual Studio Code — свободный исходный код редактор, разработанный компанией Microsoft для Windows, Linux и macOS. Поддерживает множество языков программирования и предоставляет широкий набор функций для разработки, отладки и тестирования приложений.', 4.7, 'free', 'Мультиязычный', 'Google', 0, 'visual-studio-code'),
(6, 6, 'Adobe Illustrator', 'Adobe Illustrator — векторный графический редактор, разработанный и выпускаемый компанией Adobe Systems. Часто используется для создания и редактирования иллюстраций, логотипов, иконок, элементов интерфейса и других векторных изображений.', 4.7, 'commercial', 'Мультиязычный', 'Adobe Systems', 0, 'adobe-illustrator'),
(7, 7, 'Adobe Photoshop', 'Adobe Photoshop — профессиональный растровый графический редактор, разработанный и выпускаемый компанией Adobe Systems. Широко используется в области фотографии, дизайна и других смежных областях.', 4.7, 'commercial', 'Мультиязычный', 'Adobe Systems', 0, 'adobe-photoshop'),
(8, 8, 'Autodesk 3ds Max', 'Autodesk 3ds Max (ранее 3D Studio MAX) — профессиональное программное обеспечение для 3D-моделирования, анимации и визуализации, разработанное компанией Autodesk. Широко используется в различных областях, включая разработку игр, киноиндустрию, архитектуру и другие.', 4.7, 'commercial', 'Мультиязычный', 'Autodesk', 0, 'autodesk-3ds-max'),
(9, 9, 'XnConvert', 'XnConvert — бесплатная программа для конвертации и обработки графических изображений. Поддерживает множество форматов файлов и предоставляет широкий набор инструментов для настройки параметров конвертации и обработки.', 4.7, 'free', 'Мультиязычный', 'XnSoft', 0, 'xnconvert'),
(10, 10, 'GIMP', 'GIMP — бесплатный растровый графический редактор с открытым исходным кодом, разрабатываемый сообществом разработчиков. Поддерживает множество функций для редактирования и создания растровых изображений.', 4.7, 'free', 'Мультиязычный', 'The GIMP Development Team', 0, 'gimp'),
(11, 11, 'Adobe After Effects', 'Adobe After Effects — программное обеспечение для создания и редактирования анимаций, визуальных эффектов, композиций и других видеоматериалов. Разработано компанией Adobe Systems.', 4.7, 'commercial', 'Мультиязычный', 'Adobe Systems', 0, 'adobe-after-effects'),
(12, 12, 'RollerCoaster Tycoon 3', 'RollerCoaster Tycoon 3 — экономическая стратегия, в которой игроку предоставляется возможность управлять парком аттракционов. Разработана компанией Frontier Developments.', 4.7, 'commercial', 'Мультиязычный', 'Frontier Developments', 0, 'rollercoaster-tycoon-3'),
(13, 13, 'Minecraft', 'Minecraft — компьютерная игра в жанре песочница с открытым миром, в которой игрок может свободно строить и разрушать блоки различных материалов. Разработана и выпущена шведским программистом Маркусом Перссоном.', 4.7, 'commercial', 'Мультиязычный', 'Google', 0, 'minecraft'),
(14, 14, 'Microsoft Flight Simulator', 'Microsoft Flight Simulator — симулятор полетов, разработанный и выпущенный компанией Microsoft. Позволяет игроку испытать ощущение полета на самолете и исследовать различные регионы мира.', 4.7, 'commercial', 'Мультиязычный', 'Google', 0, 'microsoft-flight-simulator'),
(15, 15, 'The Legend of Zelda: Breath of the Wild', 'The Legend of Zelda: Breath of the Wild — приключенческая игра, разработанная и выпущенная компанией Nintendo. Является частью серии игр The Legend of Zelda.', 4.7, 'commercial', 'Мультиязычный', 'Nintendo', 0, 'the-legend-of-zelda-breath-of-the-wild'),
(16, 16, 'The Witcher 3: Wild Hunt', 'The Witcher 3: Wild Hunt — ролевая игра с открытым миром, разработанная и выпущенная польской компанией CD Projekt. Основана на серии книг Анджея Сапковского.', 4.7, 'commercial', 'Мультиязычный', 'CD Projekt', 0, 'the-witcher-3-wild-hunt'),
(17, 17, 'Pac-Man', 'Pac-Man — аркадная игра, разработанная компанией Namco и выпущенная в 1980 году. Стала одной из самых известных и популярных игр в истории видеоигр.', 4.7, 'commercial', 'Мультиязычный', 'Namco', 0, 'pac-man'),
(18, 18, 'Sublime Text', 'Sublime Text — расширяемый текстовый редактор для программирования и веб-разработки. Предоставляет множество функций и возможностей для работы с кодом.', 4.7, 'commercial', 'Мультиязычный', 'Sublime HQ Pty Ltd', 0, 'sublime-text'),
(19, 19, 'Android Studio', 'Android Studio — интегрированная среда разработки (IDE) для разработки приложений под операционную систему Android. Разработана компанией Google.', 4.7, 'free', 'Мультиязычный', 'Google', 0, 'android-studio'),
(20, 20, 'MySQL', 'MySQL — свободная реляционная система управления базами данных (СУБД). Широко используется веб-приложениями для хранения и управления данными.', 4.7, 'free', 'Мультиязычный', 'Oracle Corporation', 0, 'mysql');

